// AccountPage.js
import React, { useState, useEffect } from 'react';
import api from '../../api';
import { useNavigate } from 'react-router-dom';
import SubsComponent from '../subscriptions/SubsComponent';
import UserWorkouts from '../workouts/UserWorkouts';
import { Box, Button, Typography, Card, CardContent } from '@mui/material';



const AccountPage = () => {
  const [userData, setUserData] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');


  const navigate = useNavigate();

  useEffect(() => {

    api.get('/user/data')
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setError("Account not found");
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account?')) {

      const userId = userData && userData.user_id;

      if (userId) {

        api.delete(`/subscription/delete/${userId}`)
          .then(() => {
            return api.delete(`/delete/${userId}`);
          })
          .then(response => {
            localStorage.clear();
            setModalMessage('Account deleted successfully');
            setShowModal(true);
            navigate('/home');
          })
          .catch(error => {
            console.error('Error deleting account:', error);
          });
      }
    };
  }
  const handleLogoutAccount = () => {
    localStorage.setItem('logoutNotification', 'true');
    localStorage.clear();
    setModalMessage('Logout successfully');
    setShowModal(true);

  };



  if (!userData || loading) {
    return (
      <div>
        {error}
      </div>

    );
  }

  return (
    <Box component='div' p={10} display={'flex'} justifyContent={'center'} alignItems={'center'}>
      {userData && (
        <Box>
          <Card variant="outlined" sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h5">Name: {userData.user_name}</Typography>
            </CardContent>
          </Card>
          <Card variant="outlined" sx={{ mb: 2 }}>
            <CardContent>
              <Box component={'div'}>
                <Typography>Email </Typography>
                {userData.user_email}
              </Box>
            </CardContent>
          </Card>
          <Card>
            <CardContent>
              <Typography variant="body2">Last workout check: {userData.last_check}</Typography>
            </CardContent>
          </Card>


          <Button variant='outlined' onClick={handleDeleteAccount} sx={{ mt: 2 }}>Delete account</Button>
          <Button variant='outlined' color='error' onClick={handleLogoutAccount} sx={{ mt: 1 }}>Logout</Button>
        </Box>
      )}
    </Box>
  );
};

export default AccountPage;
