// AccountPage.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import 'bootstrap/dist/css/bootstrap.css';


const AccountPage = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    
    api.get('/user/data')
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);
  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account?')) {
      // Assuming there's a 'user_id' field in the user data
      const userId = userData && userData.user_id;

      if (userId) {
        api.delete(`/delete/${userId}`)
          .then(response => {
            // Handle successful deletion (e.g., navigate to another page)
            console.log('Account deleted successfully');
          })
          .catch(error => {
            console.error('Error deleting account:', error);
          });
      }
    }
  };
  return (
    <div className='container mt-5'>
      <h1>Account Page</h1>
      {userData && (
        <div>
          <p>Name: {userData.user_name}</p>
          <p>Email: {userData.user_email}</p>
          <p>Last workout check: {userData.last_check}</p>
          <button type="button"  className="btn btn-danger" onClick={handleDeleteAccount}>Delete account</button>
          <button type="button" className="btn btn-primary">Logout</button>

        </div>
      )}
    </div>
  );
};

export default AccountPage;
