import api from '../api';
import React, { useEffect, useState } from 'react';
import styles from "./HomePage.module.css";
import { Link } from 'react-router-dom';
import { Navigate, useNavigate } from 'react-router-dom';
import { Button, ModalBody, ModalHeader, Modal } from 'react-bootstrap';

const HomePage = () => {
  const [showLogoutNotification, setShowLogoutNotification] = useState(false);

  useEffect(() => {
    const logoutNotification = localStorage.getItem('logoutNotification');
    console.log(logoutNotification);
    if (logoutNotification) {
      setShowLogoutNotification(true);
      localStorage.removeItem('logoutNotification');
    }
  }, []);

  const navigate = useNavigate();
  handleCloseModal = () => {

  }
  return (
    <div className={`${styles.container} mt-5`}>
      
      {showLogoutNotification && (
        // <div className={`${styles.alert} alert-danger`} role="alert">
        //   <>
        // </div>
        <div className={styles.modal}>
              <Modal show={showLogoutNotification} onHide={handleCloseModal}>
          <ModalHeader></ModalHeader>
          <ModalBody>
            <p>You are not authorized</p>
            <p>Please log in</p>
            <Button onClick={handleCloseModal}>Close modal</Button>
          </ModalBody>
        </Modal>
          </div>
    
      )}
      <header>
        <div>
          <h1 className={styles.title}>Welcome to home page</h1>
          <nav>
            <ul className={styles.navList}>
              {/* <li><Link to="/" className={styles.navLink}>Home</Link></li> */}
              <li><Link to="/my-workouts" className={styles.navLink}>My workouts</Link></li>
              <li><Link to="/create-workout" className={styles.navLink}>Create workout</Link></li>
              {!showLogoutNotification && (
               <li><Link to="/account" className={styles.navLink}>Account</Link></li>

              )}
              <li><Link to="/plans" className={styles.navLink}>Plans</Link></li>
              <li><Link to="/login" className={styles.navLink}>Authorize</Link></li>
            </ul>
          </nav>
        </div>
      </header>
    </div>
  );
};

export default HomePage;
