import React, { useState, useEffect } from 'react';
import styles from './UserWorkouts.module.css'; 
import api from '../../api';
import { Button, ModalBody, ModalHeader, Modal } from 'react-bootstrap';

const UserWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [modal, setModalState] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [updWorkout, setUpdWorkout] = useState({
    id: null,
    title: '',
    date: '',
    image_path: '',
  });

  const fetchUserWorkouts = async () => {
    try {
      const response = await api.get('/user/get/workouts');
      setWorkouts(response.data.workouts);
      console.log(response.data.workouts[0].image_paths);
      
    } catch (error) {
      console.error('Error fetching user workouts:', error);
    }
  };

  useEffect(() => {
    fetchUserWorkouts();
  }, []); 

  const handleDelete = async (workoutId) => {
    try {
      await api.delete(`/workouts/${workoutId}`);
      setWorkouts(prevWorkouts => prevWorkouts.filter(workout => workout.id !== workoutId));
      console.log('Successfully deleted workout', workoutId);
      setModalState(true);
    } catch (error) {
      console.error('Error deleting workout:', error);
    }
  };

  const handleUpdate = (workout) => {
    setUpdWorkout({
      id: workout.id,
      title: workout.title,
      date: workout.date,
      image_path: workout.image_path,
    });
    console.log('Image Path:', workout.image_path);
    setIsUpdating(true);
  };

  const handleUpdateSubmit = async () => {
    try {
      await api.put(`/workouts/${updWorkout.id}`, updWorkout);
      setIsUpdating(false);
      setUpdWorkout({ id: null, title: '', date: '', image_path: '' });
      fetchUserWorkouts();
    } catch (error) {
      console.error('Error updating workout:', error);
    }
  };
  const handleScroll = () => {
    

  }

  return (
    <div>
      <div className={styles.container}>
        <h2 className={styles.title}>All Users' Workouts</h2>
        {Array.isArray(workouts) &&
          workouts.map((workout) => (
            <div key={workout.id} className={styles.workoutCard}>
              <h3>{workout.title}</h3>
              <p>{workout.date}</p>
              {workout.image_paths && (
                <img src={`http://project-workouts.local/storage/${workout.image_paths}`} alt='Workout Image'></img>
              )}
              <Button type='button' onClick={() => handleDelete(workout.id)}>Delete</Button>
              <Button type='button' onClick={() => handleUpdate(workout)}>Update workout</Button>
              <button className={styles.scrollButton} onClick={handleScroll(200)}>Scroll right</button>

            </div>
          ))}
      </div>

      {isUpdating && (
        <div className="container mt-5">
          <form>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">
                Title
              </label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={updWorkout.title}
                onChange={(e) => setUpdWorkout({ ...updWorkout, title: e.target.value })}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="date" className="form-label">
                Date
              </label>
              <input
                type="text"
                className="form-control"
                id="date"
                name="date"
                value={updWorkout.date}
                onChange={(e) => setUpdWorkout({ ...updWorkout, date: e.target.value })}
              />
            </div>
            <button type="button" onClick={handleUpdateSubmit} className="btn btn-primary">
              Update workout
            </button>

          </form>
        </div>
      )}

      {modal && (
        <Modal show={modal}>
          <ModalHeader>Deleted Workout
            <Button onClick={() => setModalState(false)}>Close</Button>
          </ModalHeader>
          <ModalBody>
            <p>Successfully deleted workout</p>
          </ModalBody>
        </Modal>
      )}
    </div>
  );
};

export default UserWorkouts;
