import api from '../api';
import React, { useEffect, useState } from 'react';
import styles from "./App.module.css";

const CreateWorkoutsForm = () => {
    const [formData, setFormData] = useState({
        title: ' ',
        description: ' ',
        date: ' ',
    });



    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };



const handleSubmit = async (e) => {
    e.preventDefault();
    try
    {
        const response = api.post("/user/workouts", formData);
        console.log((await response).data)

    }
    catch (error)
    {
        console.log("Create workout error", error.response.data.message);
    }
};

    return (
        <form onSubmit={handleSubmit}>
             <div>
             <label>Title</label>
             <input type='text' name='title' onChange={handleChange}></input>
             <label>Description</label>
             <input type='text' name='description' onChange={handleChange}></input>
             <label>Date</label>
             <input name='date' onChange={handleChange}></input>
             <button className={styles.primaryBtn}>Add</button>
        </div>
        </form>
       
       
    );
    
}
export default CreateWorkoutsForm;
