import React, { useEffect, useState } from "react";
import api from "../api";
import styles from "./SubscriptionPage.module.css";

const SubscriptionPage = () => {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);

    let count = 1;

    useEffect(() => {
      
        api.get('/user/plans').then(response => {
            // console.log(response)
            setPlanData(response.data['All plans: '] || []);

        });
      
    }, []);
    const handleSubscribe = () => {
        const userId = localStorage.getItem("user_id");
        console.log(userId);
        const planId = selectedPlan.id;
    }
    return (
        <div>
        
                <div className={styles.container}>
                 
                      {planData.map(plan => (
                    <div key={plan.id} className={styles.childs}>
                            <h3>Available plan {count++} </h3>
                            <div>Name: {plan.name}</div>
                            <div>Price: {plan.price}</div>
                            <div>Duration: {plan.duration_month}</div>
                            <button
                            type="button"
                            className="btn btn-success"
                            onClick={() => setSelectedPlan(plan)}
                            >
              Select Plan
            </button>
                        
                    </div>
                    
                    ))}
                    
                  

                </div>
         


                <button
                type="button"
                className="btn btn-primary"
                onClick={handleSubscribe}
                disabled={!selectedPlan}
      >
        Subscribe on Plan
      </button>
            
        </div>
      

    );

};


export default SubscriptionPage;
