import * as React from 'react';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import InputBase from '@mui/material/InputBase';
import Badge from '@mui/material/Badge';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import NotificationsIcon from '@mui/icons-material/Notifications';
import MoreIcon from '@mui/icons-material/MoreVert';
import { useNavigate } from 'react-router-dom';
import { CustomDialog } from '../CustomDialog';
import { BASE_URL } from '../constants';
import { Avatar, Link } from '@mui/material';
import { extractFileName } from '../utils/avatarUtils';
import { useEffect, useState } from 'react';
import Notify from '../notifictations/Notifies';

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
        backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(3),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: '20ch',
        },
    },
}));

export default function NavBar() {
    const [isOpen, setIsOpen] = React.useState(false);
    const [isNotificationsOpen, setIsNotificationsOpen] = React.useState(false);
    const [avatar, setAvatar] = useState("");

    const handleDialogOpen = () => { setIsOpen(true) };
    const handleDialogClose = () => { setIsOpen(false) };
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState(null);
    const navigate = useNavigate();
    const isMenuOpen = Boolean(anchorEl);
    const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
    React.useEffect(() => {
        const storedAvatar = localStorage.getItem('avatar');
        if (storedAvatar) {

            const image = extractFileName(storedAvatar);
            setAvatar(image);
        }
    }, []);
    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

   

    const handleMenuClose = () => {
        setAnchorEl(null);
      
    };

    const handleNotifyOpen = () => {
        setIsNotificationsOpen(true);
    };

 

  

    const handleMyAccountClick = () => {
        handleMenuClose();
        const userToken = localStorage.getItem('user_token');
        if (userToken == null) {
            console.log(userToken);
            setIsOpen(true);
        }
        else {
            setIsOpen(true);
            navigate('/account');
        }
    };

    const menuId = 'primary-search-account-menu';
    const renderMenu = (
       
         <Menu
            anchorEl={anchorEl}
            anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            id={menuId}
            keepMounted
            transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
            }}
            open={isMenuOpen}
            onClose={handleMenuClose}
        >
            
            <MenuItem onClick={handleMyAccountClick}>My account</MenuItem>
            <MenuItem>Subscriptions</MenuItem>
            <MenuItem>Calculator</MenuItem>
        </Menu>
       
        
       
    );


    

    return (
        <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static" sx={{ backgroundColor: 'gray' }}>
                <Toolbar>
                    <IconButton
                        size="large"
                        edge="start"
                        color="inherit"
                        aria-label="open drawer"
                        sx={{ mr: 2 }}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography
                        variant="h6"
                        noWrap
                        component="div"
                        sx={{ display: { xs: 'none', sm: 'block' } }}
                    >
                        Home
                    </Typography>
                    
                    <Search>
                        <SearchIconWrapper>
                            <SearchIcon />
                        </SearchIconWrapper>
                        <StyledInputBase
                            placeholder="Search…"
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </Search>
                    <Box sx={{ flexGrow: 1 }} />
                    <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
                        <IconButton
                            onClick={handleNotifyOpen}
                            size="large"
                            aria-label="show 17 new notifications"
                            color="inherit"
                        >
                            <Badge badgeContent={5} color="error">
                                <NotificationsIcon />
                            </Badge>
                        </IconButton>
                        <IconButton
                            size="large"
                            edge="end"
                            aria-label="account of current user"
                            aria-controls={menuId}
                            aria-haspopup="true"
                            onClick={handleProfileMenuOpen}
                            color="inherit"
                        >
                            {avatar ?
                                <Avatar alt='User' src={`${BASE_URL}storage/${avatar}`}></Avatar> : <AccountCircle fontSize='large'></AccountCircle>
                            }
                        </IconButton>
                    </Box>
                    
                </Toolbar>
            </AppBar>
           
            {renderMenu}
            <CustomDialog isOpen={isOpen} handleClose={handleDialogClose} title='You are not authorized'>
                <Link component="button" variant='body2' onClick={() => { navigate('/login') }}>Authorize</Link>
            </CustomDialog>
            
        </Box>
    );
}
