import React, { useEffect, useState } from "react";
import styles from './CatalogPage.module.css';

import api from "../../api";

const CatalogPage = () => {
    const [catalogData, setCatalogData] = useState([]);


    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/catalog');
                if (response.data) {
                    setCatalogData(response.data.data); // Assuming your data is nested under the 'data' key
                } else {
                    console.log("Response data is undefined:", response);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // Empty array as second parameter ensures this effect runs only once

    return (
        <div className="container text-center">
        <div className="row">
            {catalogData.map(item => (
                <div className="col-md-6" key={item.id}>
                    <h2>{item.name}</h2>
                    <p>{item.description}</p>
                    <div className="d-flex justify-content-between align-items-center">
                        <p className="content">Difficulty: {item.difficulty}</p>
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star mb-1" viewBox="0 0 16 16">
                            <path d="M2.866 14.85c-.078.444.36.791.746.593l4.39-2.256 4.389 2.256c.386.198.824-.149.746-.592l-.83-4.73 3.522-3.356c.33-.314.16-.888-.282-.95l-4.898-.696L8.465.792a.513.513 0 0 0-.927 0L5.354 5.12l-4.898.696c-.441.062-.612.636-.283.95l3.523 3.356-.83 4.73zm4.905-2.767-3.686 1.894.694-3.957a.56.56 0 0 0-.163-.505L1.71 6.745l4.052-.576a.53.53 0 0 0 .393-.288L8 2.223l1.847 3.658a.53.53 0 0 0 .393.288l4.052.575-2.906 2.77a.56.56 0 0 0-.163.506l.694 3.957-3.686-1.894a.5.5 0 0 0-.461 0z" />
                        </svg>
                    </div>
                    <video autoPlay="autoplay" className="w-50">
                        <source src={`${item.video_link}`} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                </div>
            ))}
        </div>
    </div>
    
    
    );

};


export default CatalogPage;
