import { useState, useEffect } from "react";
import api from "../api";

const SubsComponent = () => {
    const [subData, setSubData] = useState([]);
    const [activeCounter, setActiveCounter] = useState(0);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/user/active-subs');
                if (Array.isArray(response.data.success)) {
                    setSubData(response.data.success);
                    const counter = response.data.success.reduce((acc, sub) => acc + (sub.active ? 1 : 0));
                    console.log(response.data.success);
                } else {
                    console.error('Invalid data format:', response.data);
                }
            } catch (error) {
                console.error('Error fetching active subscriptions:', error);
            }
        };
    
        fetchData();
    }, []);
    

    return (
        <div>
            <p>Active subs</p>
            {subData.map(sub => (
              
                
                <div key={sub.id}>
                  
                    <strong>Activated plans:</strong> {sub.active}
                </div>
            ))}
        </div>
    );
};

export default SubsComponent;
