import React, { useState, useEffect } from 'react';
//import 'bootstrap/dist/css/bootstrap.min.css';
import api from '../api';
import { Modal } from 'react-bootstrap';
import { createBrowserHistory } from 'history';

const UserRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const [showModal, setShowModal] = useState(false);

  const history = createBrowserHistory();
  let modalInstance; // Declare a variable to store the modal instance

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    const modalElement = document.getElementById('registrationModal');
    modalInstance = new Modal(modalElement);

    // Show the modal when the component mounts
    modalInstance.show();

    // Cleanup function to destroy the modal instance when the component is unmounted
    return () => {
      modalInstance.dispose();
    };
  }, []);

  const handleModalClose = () => {
    setShowModal(false);
    history.push('/home'); // Redirect to the main page after closing the modal
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await api.post("/register", formData);
      const { token } = response.data;

      localStorage.setItem('user_token', token);
      setShowModal(true);
    } catch (error) {
      console.error('Registration error:', error.response.data.message);
    }
  };

  return (
    <div className="container mt-5">
      <form onSubmit={handleSubmit}>
    


        <button type="submit" className="btn btn-primary">Register</button>
      </form>
      {/* Modal */}
      <div className="modal fade" id="registrationModal" tabIndex="-1" aria-labelledby="registrationModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="registrationModalLabel">Registration Successful</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={handleModalClose}></button>
            </div>
            <div className="modal-body">
              <p>Your registration was successful.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserRegistration;
