import api from '../api';
import React, { useEffect, useState } from 'react';
import styles from "./Modal.module.css";
import { createBrowserHistory } from 'history';
const CreateWorkoutsForm = () => {
    const [formData, setFormData] = useState({
        title: ' ',
        description: ' ',
        date: ' ',
    });
    const [isModalOpen, setModalOpen] = useState(true); // Initially set to true
    const history = createBrowserHistory();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };

      const handleAddBtnClick = async () => {
        try {
          const response = await api.post("/user/workouts", formData);
          console.log(response.data);
      
          // Close the modal
          setModalOpen(false);
      
          // Redirect to the main page
          history.push('/home');
        } catch (error) {
          console.log("Create workout error", error.response.data.message);
        }
      };
      

const handleSubmit = async (e) => {
    e.preventDefault();
    try
    {
        const response = await api.post("/user/workouts", formData);
        console.log(response.data);
        

    }
    catch (error)
    {
        console.log("Create workout error", error.response.data.message);
    }
};
    // const handleAddBtnClick = () => {
    //     history.push("/home");
    // }

    return (
        <form onSubmit={handleSubmit}>
             <div className={styles.centered}>
                <div className={styles.modalContent}>
                    </div>                
             <label>Title</label>
             <input type='text' name='title' onChange={handleChange}></input>

             <label>Description</label>
             <input type='text' name='description' onChange={handleChange}></input>
             <label>Date</label>
             <input name='date' onChange={handleChange}></input>
             <button className={styles.primaryBtn} onClick={handleAddBtnClick}>Add</button>
             <div>
           
             </div>
            
        </div>
        </form>
       
       
    );
    
}
export default CreateWorkoutsForm;
