// UserWorkouts.js

import React, { useState, useEffect } from 'react';
import styles from './UserWorkouts.module.css'; // Import your CSS module
import api from '../api';
import { Button } from 'react-bootstrap';
import DeleteWorkout from './DeleteWorkout';

const UserWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);

  useEffect(() => {
    const fetchUserWorkouts = async () => {
      try {
        const response = await api.get('/user/get/workouts');
       // console.log(response.data)
        setWorkouts(response.data.workouts); 
      } catch (error) {
        console.error('Error fetching user workouts:', error);
      }
    };

    fetchUserWorkouts();
  }, []); // Empty dependency array ensures the effect runs once on component mount

  const DeleteWorkout = (workoutId) => {
    api.delete(`/workouts/${workoutId}`).then(
      console.log('Successfully deleted workout', workouts.data.id)
    ).catch(error => {
      console.log(error);
    });
  }
  
  return (
    <div className={styles.container}>
      <h2 className={styles.title}>All Users' Workouts</h2>
      {Array.isArray(workouts) &&
        workouts.map((workout, index) => (
          <div key={index} className={styles.workoutCard}>
            <h3>{workout.title}</h3>
            <p>{workout.date}</p>
            <Button type='submit' onClick={DeleteWorkout(index)}>Delete</Button>
          </div>
        ))}
    </div>
  );
};

export default UserWorkouts;
