import { Container, FormControl, Typography, Input, InputLabel, FormHelperText, Box, Select, MenuItem, OutlinedInput } from "@mui/material";
import { useEffect, useState } from "react";
import api from "../../api";



export default function Request() {
    const [trainers, setTrainers] = useState([]);
    const [info, setInfo] = useD
    const getTrainer = async () => {
        try {
            const response = await api.get('/trainers');
            // console.log(response);
            setTrainers(response.data.trainers);
        } catch (error) {
            console.error("Error fetching trainers:", error);
        }
    };
    const handleChange = (event) => {
        const {
            target: { value },
        } = event;
        console.log(event)
        setTrainers(
            typeof value === 'string' ? value.split(',') : value,
        );
    };

    useEffect(() => {
        getTrainer()
        // console.log(trainers)
    }, [])

    return (
        <>
            <Container component='main' maxWidth='xs'>

                <Box sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',

                }}>
                    <Typography>Leave a request for training</Typography>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel htmlFor="my-input-1">Name</InputLabel>
                        <Input id="my-input-1" aria-describedby="my-helper-text" />
                    </FormControl>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel htmlFor="my-input-2">Email</InputLabel>
                        <Input id="my-input-2" aria-describedby="my-helper-text" />
                        <FormHelperText id="my-helper-text">We'll never share your email.</FormHelperText>
                    </FormControl>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel id="demo-multiple-name-label">Select a trainer</InputLabel>
                        <Select labelId="demo-multiple-name-label" onChange={handleChange} value={trainers} input={<OutlinedInput label="Name" />}>
                            {trainers.map((trainer) => (
                                <MenuItem key={trainer.id} value={trainer.name}>
                                    {trainer.name} {trainer.surname}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Box>






            </Container>
        </>
    );
}
