import React, { useEffect, useState } from "react";
import api from "../../api";
import styles from "./SubscriptionPage.module.css";
import { redirect } from "react-router-dom";
import { Navigate, useNavigate } from 'react-router-dom';

export default function SubscriptionPage () {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);

    const redirectToPlansPage = (plan) => {
        console.log(plan);
        navigate(`/plans/${plan.id}`, { state: { planData: plan } });
    }
    const closeModal = () => {
        setShowModal(false);
        setErrorMessage('');
    }
    return (
        <div>

            <div className="container text-center">
                <div className="row align-items-center">
                    {planData.map(plan => (
                        <div key={plan.id} className="col">

                            <div>Name: {plan.name}</div>
                            <div>Price: {plan.price}</div>
                            <div>Duration: {plan.duration_month}</div>
                            <button
                                type="button"
                                className="btn btn-secondary"
                                onClick={() => redirectToPlansPage(plan)}
                            >
                                Select Plan
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};
