import { Container, FormControl, Typography, Input, InputLabel, FormHelperText, Box, Select, MenuItem, OutlinedInput, Button } from "@mui/material";
import { useEffect, useState } from "react";
import api from "../../api";



export default function Request() {
    const [trainers, setTrainers] = useState([]);
    const [info, setInfo] = useState('');
    const [userData, setUserData] = useState({
        name: '',
        phone: '',
        email: '',
        trainer: info.name,
    });
    const getTrainer = async () => {
        try {
            const response = await api.get('/trainers');
            setTrainers(response.data.trainers);
        } catch (error) {
            console.error("Error fetching trainers:", error);
        }
    };

    const handleChange = (event) => {
        const {
            target: { value },
        } = event;
        const selectedTrainer = trainers.find(trainer => trainer.name === value);
        setInfo(
            selectedTrainer ? { name: selectedTrainer.name, surname: selectedTrainer.surname, id: selectedTrainer.id } : ''
        );
    };

    useEffect(() => {
        getTrainer();
        console.log(info);
    }, [info]);

    const handleSend = async () => {
        try {
            const formDataToSend = new FormData();
            formDataToSend.append('user_name', userData.name);
            formDataToSend.append('email', userData.email);
            formDataToSend.append('phone', userData.phone);
            formDataToSend.append('trainer_name', userData.trainer);
            const response = api.post(`/store/request/${info.id}`, formDataToSend, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            console.log(response);
        }
        catch (error) {
            console.log(error);
        }
    }

    return (
        <>
            <Container component='main' maxWidth='xs'>
                <Box sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}>
                    <Typography>Leave a request for training</Typography>
                    <FormControl sx={{ width: 290, m: 1 }}>
                        <InputLabel htmlFor="my-input-1">Name</InputLabel>
                        <Input id="my-input-1" aria-describedby="my-helper-text" value={userData.name} />
                    </FormControl>
                    <FormControl sx={{ width: 290, m: 1 }}>
                        <InputLabel htmlFor="my-input-2">Phone</InputLabel>
                        <Input id="my-input-2" aria-describedby="my-helper-text" value={userData.phone} />
                        <FormHelperText id="my-helper-text">We'll never share your phone number.</FormHelperText>
                    </FormControl>
                    <FormControl sx={{ width: 290, m: 1 }}>
                        <InputLabel htmlFor="my-input-3">Email</InputLabel>
                        <Input id="my-input-3" aria-describedby="my-helper-text" value={userData.email} />
                    </FormControl>
                    <FormControl sx={{ width: 290, m: 1 }}>
                        <InputLabel id="demo-multiple-name-label">Select a trainer</InputLabel>
                        <Select
                            labelId="demo-multiple-name-label"
                            onChange={handleChange}
                            value={info}
                            input={<OutlinedInput label="Name" />}
                        >
                            {trainers.map((trainer) => (
                                <MenuItem key={trainer.id} value={trainer.name}>
                                    {trainer.name} {trainer.surname}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <Button onClick={handleSend}>Send</Button>
                </Box>
            </Container>
        </>
    );
}
