import api from '../../api';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Navigate, useNavigate } from 'react-router-dom';
import SubscriptionPage from '../subscriptions/SubscriptionPage';
import PrimarySearchAppBar from './ResponsiveAppBar';


const HomePage = () => {
  const [showLogoutNotification, setShowLogoutNotification] = useState(false);
  const [notifyState, setNotifyState] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const handleUserAccountClick = () => {
    const userToken = localStorage.getItem('user_token');

    if (userToken == null) {
      // alert('hehehe')
      console.log(userToken);
      setShowModal(true);
    }
    else {
      navigate('/account');
    }
  };


  useEffect(() => {
    const logoutNotification = localStorage.getItem('logoutNotification');
    const notify = localStorage.getItem('notifyState');
    console.log(localStorage.getItem("user_token"));
    console.log(notify);
    if (notify === true) {
      setNotifyState(true);
    }

    if (logoutNotification) {
      setShowLogoutNotification(true);
      localStorage.removeItem('logoutNotification');
    }
  }, []);

  const navigate = useNavigate();
  const handleModalClose = () => {
    setShowModal(false);


  };

  return (
    <>

      <PrimarySearchAppBar></PrimarySearchAppBar>
      {/* <CatalogPage></CatalogPage> */}
      <div class="card text-center">

        <div class="card-body">
          <h5 class="card-title">Special title treatment</h5>
          <p class="card-text">Checkout for ready workout setups for ya!</p>

        </div>
        <div class="card-footer text-muted">
          Featured
        </div>
      </div>
      {/* <SignIn></SignIn> */}
      <SubscriptionPage></SubscriptionPage>

      {/* <Modal show={showModal} onHide={handleModalClose} className={styles.modal}>
        <Modal.Header closeButton>
          <Modal.Title>Not authorized</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Please authorize.</p>
        </Modal.Body>
        <Modal.Footer>
          <Link to='/login'>Authorize here</Link>
          
        </Modal.Footer>
      </Modal> */}

    </>

  );

};

export default HomePage;
