import React, { useEffect, useState } from "react";
import api from "../../api";
import styles from "./SubscriptionPage.module.css";
import { Modal, Button } from "react-bootstrap";

const SubscriptionPage = () => {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    let count = 1;
    let temp = true;
    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);

    const handleSubscribe = () => {
        const planId = selectedPlan.id;
        if (planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    console.log(response);
                    setErrorMessage("Success");
                    setShowModal(true);
                })
                .catch(error => {
                    setErrorMessage(error.response.data.error);
                    setShowModal(true);

                });
        }
    }


    const closeModal = () => {
        setShowModal(false);
        setErrorMessage(''); // Reset the error message when closing the modal
    }

    return (
        <div>
            <div class="card" style="width: 18rem;">
                <div class="card-body">
                    <h5 class="card-title">Card title</h5>
                    <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6>
                    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    <a href="#" class="card-link">Card link</a>
                    <a href="#" class="card-link">Another link</a>
                </div>
            </div>
            <div className="container text-center">
                {planData.map(plan => (
                    <div key={plan.id} className="col align-self-center">
                        <h3>Available plan {count++} </h3>
                        <div>Name: {plan.name}</div>
                        <div>Price: {plan.price}</div>
                        <div>Duration: {plan.duration_month}</div>
                        <button
                            type="button"
                            className="btn btn-success"
                            onClick={() => setSelectedPlan(plan)}
                        >
                            Select Plan
                        </button>
                    </div>
                ))}
            </div>

            {/* <button
                type="button"
                className="btn btn-primary"
                onClick={handleSubscribe}
                disabled={!selectedPlan}
            >
                Subscribe on Plan
            </button> */}

            {/* React Bootstrap Modal */}
            <Modal show={modal} onHide={closeModal}>
                <Modal.Header closeButton>
                    {/* <Modal.Title>Error</Modal.Title> */}
                </Modal.Header>
                <Modal.Body>
                    {temp ? (
                        <p>{errorMessage}</p>
                    ) : (
                        <p>Succes</p>
                    )}

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={closeModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>


        </div>
    );
};

export default SubscriptionPage;
