import { useEffect, useState } from "react";
import api from "../../api";
import styles from "./FeedBack.module.css";
import Star from "./Star";
import { BASE_URL } from "../constants";
import { Button, CardText, FormText } from "react-bootstrap";
import InputGroupText from "react-bootstrap/esm/InputGroupText";
import { getValue } from "@testing-library/user-event/dist/utils";

const FeedBack = (props) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get(`feedbacks/${props.planId}`);
                console.log(response.data.success);
                setData(response.data.success);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [props.planId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }
    const handleChange = (val) => {
        console.log(val)
    }
    const renderStars = (rating) => {
        let stars = [];
        for (let i = 1; i <= 5; i++) {
            stars.push(<Star key={i} filled={i <= rating} />);
        }
        return stars;
    };

    return (
        <div>
            {data.map((item) => (
                <div key={item.id} className={styles["comment-container"]}>
                    <img src={`${BASE_URL}storage/${item.avatar}`} alt="Cannot display image" width={50}></img>
                    <div>{item.user.name}</div>
                    <div className={styles["comment-header"]}>{item.name}</div>
                    <div className={styles["comment-body"]}>{item.comment}</div>
                    <div>Rating: {renderStars(item.rating)}</div>
                    <div className={styles["comment-footer"]}>Posted on: {item.created_at}</div>
                
                </div>
            ))}
            
            <textarea placeholder="Add comment here" onInput={handleChange()}></textarea>
            {/* {console.log(item.target)} */}
            <Button>send</Button>
            
        </div>
    );
};

export default FeedBack;
