import React from "react";
import api from "../../api";
import {
    format,
    startOfWeek,
    addDays,
    startOfMonth,
    endOfMonth,
    endOfWeek,
    isSameMonth,
    isSameDay,
    parse,
    addMonths,
    subMonths,
    startOfDay
} from "date-fns";

class Calendar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentMonth: new Date(),
            selectedDate: new Date(),
            workoutDate: [],
        };
    }

    componentDidMount() {
        api.get(`/schedule`).then((response) => {
            const responseData = response.data;
            if (Array.isArray(responseData.date)) {
                const workoutDates = responseData.date.map((workout) => parse(workout.date, "yyyy.MM.dd", new Date()));
                this.setState({
                    workoutDate: workoutDates,
                });
            } else {
                console.error("Dates array not found in API response.");
            }
        });
    }
    


    onDateClick = (day) => {
        this.setState({
            selectedDate: day,
        });
    };

    nextMonth = () => {
        this.setState({
            currentMonth: addMonths(this.state.currentMonth, 1),
        });
    };

    prevMonth = () => {
        this.setState({
            currentMonth: subMonths(this.state.currentMonth, 1),
        });
    };

    renderHeader() {
        const dateFormat = "MMMM yyyy";

        return (
            <div className="header row flex-middle">
                <div className="col col-start">
                    <div className="icon" onClick={this.prevMonth}>
                        chevron_left
                    </div>
                </div>
                <div className="col col-center">
                    <span>{format(this.state.currentMonth, dateFormat)}</span>
                </div>
                <div className="col col-end" onClick={this.nextMonth}>
                    <div className="icon">chevron_right</div>
                </div>
            </div>
        );
    }

    renderDays() {
        const dateFormat = "dddd";
        const days = [];

        let startDate = startOfWeek(this.state.currentMonth);

        for (let i = 0; i < 7; i++) {
            days.push(
                <div className="col col-center" key={i}>
                    {format(addDays(startDate, i), dateFormat)}
                </div>
            );
        }

        return <div className="days row">{days}</div>;
    }

    renderCells() {
        const { currentMonth, selectedDate } = this.state;
        const monthStart = startOfMonth(currentMonth);
        const monthEnd = endOfMonth(monthStart);
        const startDate = startOfWeek(monthStart);
        const endDate = endOfWeek(monthEnd);

        const dateFormat = "d";
        const rows = [];

        let days = [];
        let day = startDate;
        let formattedDate = "";

        while (day <= endDate) {
            for (let i = 0; i < 7; i++) {
                formattedDate = format(day, dateFormat);
                const cloneDay = day;
                const hasWorkout = this.state.workoutDate.some((date) =>
                    isSameDay(startOfDay(date), startOfDay(cloneDay))
                );
                console.log(`Date: ${cloneDay}, hasWorkout: ${hasWorkout}`);

                days.push(
                    <div
                        className={`col cell ${!isSameMonth(day, monthStart)
                            ? "disabled"
                            : isSameDay(day, selectedDate) ? "selected" : ""
                            } ${hasWorkout ? "has-workout" : ""}`}
                        key={day}
                        onClick={() => this.onDateClick(parse(cloneDay))}
                    >
                        <span className="number">{formattedDate}</span>
                        <span className="bg">{formattedDate}</span>
                    </div>
                );
                day = addDays(day, 1);
            }
            rows.push(
                <div className="row" key={day}>
                    {days}
                </div>
            );
            days = [];
        }
        return <div className="body">{rows}</div>;
    }

    render() {
        return (
            <div className="calendar">
                {this.renderHeader()}
                {/* {this.renderDays()} */}
                {this.renderCells()}
            </div>
        );
    }
}

export default Calendar;
