import React, { useEffect, useState } from "react";
import api from "../api";
import styles from "./SubscriptionPage.module.css";

const SubscriptionPage = () => {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    let count = 1;

    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);

    const handleSubscribe = () => {
        const planId = selectedPlan.id;
        if(planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    console.log(response);
                })
                .catch(error => {
                    setErrorMessage('Error subscribing to the plan: ' + error.message);
                    setShowModal(true);
                    console.error('Error subscribing to the plan:', error);
                });
        }
    }

    const closeModal = () => {
        setShowModal(false);
        setErrorMessage(''); // Reset the error message when closing the modal
    }

    return (
        <div>
            <div className={styles.container}>
                {planData.map(plan => (
                    <div key={plan.id} className={styles.childs}>
                        <h3>Available plan {count++} </h3>
                        <div>Name: {plan.name}</div>
                        <div>Price: {plan.price}</div>
                        <div>Duration: {plan.duration_month}</div>
                        <button
                            type="button"
                            className="btn btn-success"
                            onClick={() => setSelectedPlan(plan)}
                        >
                            Select Plan
                        </button>
                    </div>
                ))}
            </div>

            <button
                type="button"
                className="btn btn-primary"
                onClick={handleSubscribe}
                disabled={!selectedPlan}
            >
                Subscribe on Plan
            </button>

            {modal && (
  <div className={styles.modal}>
    <div className={styles.modal-content}>
      <span className={styles.close} onClick={closeModal}>&times;</span>
      <p>{errorMessage}</p>
    </div>
  </div>
)}

        </div>
    );
};

export default SubscriptionPage;
