// src/components/HomePage.js

import api from '../api';
import React, { useEffect, useState } from 'react';


//const response = await axios.get("http://127.0.0.1:8000/api/user/data")

// const data = response.data;
//console.log(data);

const HomePage = () => {
    const [userData, setUserData] = useState(null);

    useEffect(()=> {
       api.get('/user/data')
       .then(response => {
        setUserData(response.data);
       })
       
    }, []);

    return (
        <div>
          {/* Ваш код для отображения данных пользователя */}
          {userData && (
            <div>
              <h1>Welcome, {userData.user_name}!</h1>
              <h2>User_id is: {userData.user_id}</h2>
              {/* Дополнительные данные о пользователе */}
            </div>
          )}
        </div>
      );
    };



export default HomePage;
