import { useState, useEffect } from "react";
import api from "../../api";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { BASE_URL } from "../constants";
import styles from "./PlansComp.module.css";
import FeedBack from "./FeedBack";
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';
import CloseIcon from '@mui/icons-material/Close';
import IconButton from '@mui/material/IconButton';

import { Avatar, Button } from "@mui/material";
const PlansComp = () => {
    const [info, setInfo] = useState(null);
    const [alert, setAlertShow] = useState(false);
    const [alertSeverity, setAlertSeverity] = useState("");
    const { planId } = useParams();
    const location = useLocation();
    const navigate = useNavigate();
    const { planData } = location.state;
    useEffect(() => {
        const fetchData = async () => {
            try {

                const response = await api.get(`plan-info/${planId}`);
                const { trainer } = response.data;
                
                setInfo(trainer);
            } catch (error) {
                console.error('Error fetching plan info:', error);
            }
        };

        fetchData();
    }, [planId]);

    const handleSubscribe = () => {

        if (planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    // navigate('/home');
                    setAlertSeverity("success");
                    console.log(response.data.error)
                })
                .catch(error => {
                    if (error.response.status == 400) {
                        setAlertSeverity("error");
                        setAlertShow(true);
                        console.log(error.response.data)
                    }
                    else {
                        
                    }

                });
        }
    }
    if (!info) {
        return <div>Loading...</div>;
    }
    return (
        <>
            <div>
            {alert && (
                <Alert 
                    icon={<CheckIcon fontSize="inherit" />} 
                    severity={alertSeverity} // Устанавливаем цвет алерта в зависимости от severity
                    sx={{ position: 'fixed', top: 0, left: 0, right: 0, zIndex: 9999 }} // Fix alert to top of screen
                >
                    {alertMessage}
                </Alert>
            )}
            
                <div>
                    <h5>Information about chosen plan</h5>
                    <p>{planData.name}</p>
                    <p>Price: {planData.price}$</p>
                    <p>Duration: {planData.duration_months} months</p>
                </div>

                <div>
                    <h5>Trainer that available on this plan</h5>
                    <Avatar src={`${BASE_URL}${info.image_path}`} sx={{width: 150, height: 150}} alt="Cannot display image"></Avatar>
                   

                    <p>{info.description}</p>
                    <p>{info.year_exp} years of experience</p>
                    <blockquote>
                        <p>{info.quote}</p>
                        <footer>{info.name} {info.surname}</footer>
                    </blockquote>


                    <Button variant="outlined" onClick={() => handleSubscribe()}>Subscribe</Button>

                </div>
                <p>Users comments subscribed to this plan</p>
                <FeedBack planId={planId} trainerId={planData.trainer_id} />

            </div>
          
            


        </>
    );
}




export default PlansComp;
