// import { Link } from 'react-router-dom';

import api from '../api';
import React, { useEffect, useState } from 'react';
import styles from "./HomePage.module.css";
import { Link } from 'react-router-dom';
import { Navigate, useNavigate } from 'react-router-dom';



const HomePage = ( ) => {
  const [showLogoutNotification, setShowLogoutNotification] = useState(false);

  useEffect(() => {
    const logoutNotification = localStorage.getItem('logoutNotification');
    console.log(logoutNotification);
    if (logoutNotification)
    {
      setShowLogoutNotification(true);
      localStorage.removeItem('logoutNotification');
    }
  }, []);
  const navigate = useNavigate();


  return (
   
    <div className='container mt-5'>
      {/* Display the logout notification if needed */}
      {showLogoutNotification && (
        <div className="alert alert-danger" role="alert">
          You are not authorized. Please log in.
        </div>
      )}
    <header>
      <div className='container'>
        <h1>Welcome to home page</h1>
        <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/my-workouts">My workouts</Link></li>
          <li><Link to="/create-workout">Create workout</Link></li>
          <li><Link to="/account">Account</Link></li>
          <li><Link to="/subscription">Subscription</Link></li>
          <li><Link to="/login">Authorize</Link></li>
        </ul>
      </nav>
      </div>
     
    </header>
    </div>
  );
};
   

    
    



export default HomePage;
