import { useEffect, useState } from "react";
import api from "../../api";
import styles from "./FeedBack.module.css";
import Star from "./Star";
import { BASE_URL } from "../constants";


const FeedBack = (props) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [inputValue, setInputValue] = useState("");
    const [rating, setRating] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get(`feedbacks/${props.planId}`);
                setData(response.data.success);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };
        const avatarka = localStorage.getItem('avatar');

        fetchData();
    }, [props.planId]);

    const handleAddComment = async () => {
        try {
            await api.post(`/add/feedback/${props.planId}/${props.trainerId}`, {
                comment: inputValue,
                rating: rating
            });
            // Очистить поле ввода и сбросить рейтинг после успешной отправки
            setInputValue("");
            setRating(0);
            // Перезагрузить данные после отправки комментария
            const response = await api.get(`feedbacks/${props.planId}`);
            setData(response.data.success);
        } catch (error) {
            console.error("Error adding comment:", error);
        }
    };

    const handleChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleRatingChange = (event) => {
        setRating(event.target.value);
    };

    const renderStars = (rating) => {
        let stars = [];
        for (let i = 1; i <= 5; i++) {
            stars.push(<Star key={i} filled={i <= rating} />);
        }
        return stars;
    };

    return (
        <div>
            {data.map((item) => (
                <div key={item.id} className={styles["comment-container"]}>
                    <img src={`${BASE_URL}storage/${item.avatar}`} alt="Cannot display image" width={50}></img>
                    <div>{item.user.name}</div>
                    <div className={styles["comment-header"]}>{item.name}</div>
                    <div className={styles["comment-body"]}>{item.comment}</div>
                    <div>Rating: {renderStars(item.rating)}</div>
                    <div className={styles["comment-footer"]}>Posted on: {item.created_at}</div>
                </div>
            ))}
            <textarea placeholder="Write comment here" value={inputValue} onChange={handleChange}></textarea>
            <input type="number" placeholder="Rating" value={rating} onChange={handleRatingChange} />
            <button onClick={handleAddComment}>Add comment</button>
        </div>
    );
};

export default FeedBack;
