import * as React from 'react';
import { useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link'; 
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import api from '../../api';
import { useNavigate } from 'react-router-dom';
import { CustomDialog } from '../CustomDialog';
import { Alert } from '@mui/material';
function SignIn() {
    const [isOpen, setIsOpen] = React.useState(false);
    const [responseData, setResponseData] = useState("");
    const [avatar, setAvatar] = useState("");
    const [alert, setAlertShow] = useState(false);
    const [alertSeverity, setAlertSeverity] = useState("");
    const [alertMessage, setAlertMessage] = useState([]);
    const [userData, setUserData] = useState({
        email: '',
        password: '',
    });
    const navigate = useNavigate();

    const handleDialogOpen = () => {
        setIsOpen(true);
    };

    const handleDialogClose = () => { 
        setIsOpen(false);
        navigate('/home');
    };

    

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await api.post('/authorize', userData);
            const { token } = response.data;
            console.log(response.data);
            localStorage.setItem('avatar', response.data.avatar);
            localStorage.setItem('user_token', token);
            setAvatar(response.data.avatar);
            setResponseData(response.data);
            setIsOpen(true);
         
        } catch (error) {
            console.error('Authorization failed:', error);
            setAlertShow(true);
            setAlertSeverity("warning");
            const parsedErrors = Object.values(error.response.data.errors).flatMap(errorArray => errorArray);
            setAlertMessage(parsedErrors);

          
        }
    };

    const handleChange = (e) => {
        setUserData({ ...userData, [e.target.name]: e.target.value });
    };

    return (
        <>
        {alert && (
                <Alert 
                     
                    severity={alertSeverity} onClose={() => {setAlertShow(false)}}
                    sx={{ position: 'fixed', top: 0, left: 0, right: 0, zIndex: 9999 }} 
                >
                    {alertMessage}
                </Alert>
            )}
            <ThemeProvider theme={createTheme()}>
                <Container component="main" maxWidth="xs">
                    <CssBaseline />
                    <Box
                        sx={{
                            marginTop: 8,
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                            <LockOutlinedIcon />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Sign in
                        </Typography>
                        <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
                            <TextField
                                value={userData.email}
                                onChange={handleChange}
                                margin="normal"
                                required
                                fullWidth
                                id="email"
                                label="Email Address"
                                name="email"
                                autoComplete="email"
                                autoFocus
                            />
                            <TextField
                                value={userData.password}
                                onChange={handleChange}
                                margin="normal"
                                required
                                fullWidth
                                name="password"
                                label="Password"
                                type="password"
                                id="password"
                                autoComplete="current-password"
                            />
                            <FormControlLabel
                                control={<Checkbox value="remember" color="primary" />}
                                label="Remember me"
                            />
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                sx={{ mt: 3, mb: 2 }}
                            >
                                Sign In
                            </Button>
                            <Grid container>
                                <Grid item xs>
                                    <Link href="#" variant="body2">
                                        Forgot password?
                                    </Link>
                                </Grid>
                                <Grid item>
                                    <Link component="button" variant="body2" onClick={() => { navigate('/register') }}>
                                        Don't have an account? Sign Up
                                    </Link>
                                </Grid>
                            </Grid>
                        </Box>
                    </Box>
                </Container>
            </ThemeProvider>
            <CustomDialog isOpen={isOpen} title='Successfully authorized' handleClose={handleDialogClose}>
                <p>{responseData.message}</p>
            </CustomDialog>
        </>
    );
}

export default SignIn;
