import api from '../../api';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Navigate, useNavigate } from 'react-router-dom';
import SubscriptionPage from '../subscriptions/SubscriptionPage';


const HomePage = () => {
  const [showLogoutNotification, setShowLogoutNotification] = useState(false);
  // const [isModalOpen, setIsModalOpen] = useState(true);
  const [notifyState, setNotifyState] = useState(false);

  const [showModal, setShowModal] = useState(false);

  // const [userToken, setUserToken] = useState(null);
  const handleUserAccountClick = () => {
    const userToken = localStorage.getItem('user_token');

    if (userToken == null) {
      // alert('hehehe')
      console.log(userToken);
      setShowModal(true);
    }
    else {
      navigate('/account');
    }
  };


  useEffect(() => {
    const logoutNotification = localStorage.getItem('logoutNotification');
    const notify = localStorage.getItem('notifyState');
    console.log(localStorage.getItem("user_token"));
    console.log(notify);
    if (notify === true) {
      setNotifyState(true);
    }

    if (logoutNotification) {
      setShowLogoutNotification(true);
      localStorage.removeItem('logoutNotification');
    }
  }, []);
  // const closeModal = () => {
  //   setIsModalOpen(false);
  // };
  const navigate = useNavigate();
  const handleModalClose = () => {
    setShowModal(false);

    // navigate('/home');
  };

  return (
    <>

      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        {/* <a className="navbar-brand" href="#">Navbar</a> */}
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            {/* <li className="nav-item">
              <Link to="/create-workout" className='nav-link'>Create workout</Link>
            </li> */}

            {/* <li className="nav-item">
              <Link to="/login" className='nav-link'>Authorize</Link>
            </li> */}
          </ul>
          <form className="form-inline my-2 my-lg-0 mr-15">
            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
            <button className="btn btn-outline-success my-2 my-sm-0 mx-2" type="submit">Search</button>
          </form>
        </div>
        <li className="nav-account">

          {/* <li><Link to="/account" onClick={handleUserAccountClick} className='nav-link'>User account <i class="fa-solid fa-user"></i></Link></li> */}


          <a onClick={handleUserAccountClick} className='nav-link'>User account <i class="fa-solid fa-user"></i></a>


        </li>


      </nav>

      {/* <CatalogPage></CatalogPage> */}
      <div class="card text-center">

        <div class="card-body">
          <h5 class="card-title">Special title treatment</h5>
          <p class="card-text">Checkout for ready workout setups for ya!</p>

        </div>
        <div class="card-footer text-muted">
          Featured
        </div>
      </div>
      {/* <SignIn></SignIn> */}
      <SubscriptionPage></SubscriptionPage>

      {/* <Modal show={showModal} onHide={handleModalClose} className={styles.modal}>
        <Modal.Header closeButton>
          <Modal.Title>Not authorized</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Please authorize.</p>
        </Modal.Body>
        <Modal.Footer>
          <Link to='/login'>Authorize here</Link>
          
        </Modal.Footer>
      </Modal> */}

    </>

  );

};

export default HomePage;
