import React, { useEffect, useState } from "react";
import api from "../../api";
import { useNavigate } from 'react-router-dom';
import { Box, Button, Typography, makeStyles } from "@mui/material";

const useStyles = makeStyles((theme) => ({
  planBox: {
    border: `1px solid ${theme.palette.primary.main}`,
    borderRadius: theme.shape.borderRadius,
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  },
  button: {
    marginTop: theme.spacing(2),
  },
}));

export default function SubscriptionPage() {
    const classes = useStyles();
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    
    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);
    
    const redirectToPlansPage = (plan) => {
        console.log(plan);
        navigate(`/plans/${plan.id}`, { state: { planData: plan } });
    }
    
    return (
        <Box component="div" display="flex" flexDirection="column">
            {planData.map(plan => (
                <Box key={plan.id} className={classes.planBox}>
                    <Typography variant="h6">{plan.name}</Typography>
                    <Typography variant="body1">Price: {plan.price}</Typography>
                    <Typography variant="body1">Duration: {plan.duration_month}</Typography>
                    <Button
                        className={classes.button}
                        variant="contained"
                        color="primary"
                        onClick={() => redirectToPlansPage(plan)}
                    >
                        Select Plan
                    </Button>
                </Box>
            ))}
        </Box>
    );
};
