import React, { useEffect, useState } from "react";
import api from "../../api";
import { useNavigate } from 'react-router-dom';
import { Box, Button } from "@mui/material";
export default function SubscriptionPage() {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);

    const redirectToPlansPage = (plan) => {
        console.log(plan);
        navigate(`/plans/${plan.id}`, { state: { planData: plan } });
    }
    return (
        <Box component="div" m={10} display={"flex"} flexDirection={"column-reverse"}>
           
               
                    {planData.map(plan => (
                        <Box key={plan.id}>

                            <Box>Name: {plan.name}</Box>
                            <Box>Price: {plan.price}</Box>
                            <Box>Duration: {plan.duration_month}</Box>
                            <Button
                                onClick={() => redirectToPlansPage(plan)}
                            >
                                Select Plan
                            </Button>
                        </Box>
                    ))}
             
        </Box>
    );
};
