import { useEffect, useState } from "react";
import api from "../../api";
import { BASE_URL } from "../constants";
import { extractFileName } from '../utils/avatarUtils';
import { Box, Button, Rating, TextField, Typography } from "@mui/material";
import ClearIcon from '@mui/icons-material/Clear';

const FeedBack = (props) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [inputValue, setInputValue] = useState("");
    const [value, setValue] = useState(0);
    const [rating, setRating] = useState(0);
    const [hoveredBoxId, setHoveredBoxId] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get(`feedbacks/${props.planId}`);
                console.log(response.data.success);
                setData(response.data.success);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [props.planId]);
    const handleDeleteComment = async (feedbackId) => {
        try {

            await api.delete(`/delete/feedback/${feedbackId}`)
                .then(response => {
                    console.log(response.data);
                })
                .finally();

        } catch (error) {
            console.error("Error deleting comment:", error);
        }
    };
    const handleAddComment = async () => {
        try {
            await api.post(`/add/feedback/${props.planId}/${props.trainerId}`, {
                comment: inputValue,
                rating: value
            });
            setInputValue("");
            setRating(0);
            const response = await api.get(`feedbacks/${props.planId}`);
            setData(response.data.success);
        } catch (error) {
            console.error("Error adding comment:", error);
        }
    };
    const handleMouseEnter = (id) => {
        setHoveredBoxId(id);
    };

    const handleMouseLeave = () => {
        setHoveredBoxId(null);
    };
    const handleChange = (event) => {
        setInputValue(event.target.value);
    };
    const formatDate = (dateStr) => {
        const date = new Date(dateStr);
        return date.toLocaleString();

    };

    return (
        <Box>
            {data.map((item) => (
                <Box key={item.id} border={1} position="relative" sx={{ marginBottom: 1 }} onMouseEnter={() => handleMouseEnter(item.id)}
                    onMouseLeave={handleMouseLeave}>
                    <img src={`${BASE_URL}storage/${extractFileName(item.avatar)}`} alt="Cannot display image" width={50} />
                    <Typography>{item.user.name}</Typography>
                    <Typography>{item.name}</Typography>
                    <Typography>{item.comment}</Typography>
                    <Typography variant="caption">Rating: <Rating name="read-only" size="small" value={item.rating} readOnly /></Typography>
                    <div>Posted on: {formatDate(item.created_at)}</div>
                    {localStorage.getItem('user_token') === item.user.remember_token && (
                        <ClearIcon color="primary" sx={{ position: 'absolute', top: 5, right: 5, cursor: 'pointer' }} onClick={() => handleDeleteComment(item.id)} />
                    )};

                </Box>
            ))}
            <TextField placeholder="Write comment here" value={inputValue} onChange={handleChange} multiline rows={5} />
            <Rating name="simple-controlled" value={value} onChange={(event, newValue) => { setValue(newValue) }} />
            <Button variant="outlined" onClick={handleAddComment}>Add comment</Button>
        </Box>

    );
};

export default FeedBack;
