import { useState, useEffect } from "react";
import api from "../../api";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { BASE_URL } from "../constants";
import styles from "./PlansComp.module.css";
import { Modal, Button, Box } from "@mui/material";
import Alert from '@mui/material/Alert';

const PlansComp = () => {
    const [info, setInfo] = useState(null);
    const { planId } = useParams();
    const [open, setModalState] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();
    const { planData } = location.state;
    useEffect(() => {
        const fetchData = async () => {
            try {

                const response = await api.get(`plan-info/${planId}`);
                const { trainer } = response.data;
                // console.log(planData);
                setInfo(trainer);
            } catch (error) {
                console.error('Error fetching plan info:', error);
            }
        };

        fetchData();
    }, [planId]);
    // const handleOpen = () => {
    //     setModalState(true);
    // };
    
    const handleClose = () => {
        setModalState(false);
    };
    
    const handleSubscribe = () => {

        if (planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    // console.log(response.data.error);
                    navigate('/home');

                })
                .catch(error => {
                    if (error.response.status == 401) {
                        console.log(error.response.data.error);
                        // console.error('User is not authorized:', error);

                    }
                    else {
                        setModalState(true);
                        // <Alert severity="success">This is a success Alert.</Alert>
                        console.log(error.response.data.error);
                      
                        console.log(open);
                        

                        // console.error('Error fetching plan info:', error);
                    }

                });
        }
    }
    if (!info) {
        return <div>Loading...</div>;
    }
    return (
        <>
            <div className={styles.container}>
                <div className={styles.planInfo}>
                    <h5>Information about chosen plan</h5>
                    <p>{planData.name}</p>
                    <p>Price: {planData.price}$</p>
                    <p>Duration: {planData.duration_months} months</p>
                </div>

                <div className={styles.trainersInfo}>
                    <h5>Trainer that available on this plan</h5>
                    <img src={`${BASE_URL}${info.image_path}`} alt="Cannot display image" width={200}></img>

                    <p>{info.description}</p>
                    <p>{info.year_exp} years of experience</p>
                    <blockquote class="blockquote text-center">
                        <p class="mb-3">{info.quote}</p>
                        <footer class="blockquote-footer">{info.name} {info.surname}</footer>
                    </blockquote>


                    <button className="btn btn-primary" onClick={() => handleSubscribe()}>Subscribe</button>
                </div>
            </div>
            
        </>
    );
}




export default PlansComp;
