import { Container, FormControl, Typography, Input, InputLabel, FormHelperText, Box, Select } from "@mui/material";
import { useEffect, useState } from "react";
import api from "../../api";



export default function Request() {
    const [trainers, setTrainers] = useState([]);

    const getTrainer = async () => {
        await api.get('/trainers').then(response => (
            // setTrainers(response.data.trainers),
            console.log(response)
        )).finally(setTrainers(response))
    };

    useEffect(() => {
        getTrainer()
    })

    return (
        <>
            <Container component='main' maxWidth='xs'>

                <Box sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',

                }}>
                    <Typography>Leave a request for training</Typography>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel htmlFor="my-input-1">Name</InputLabel>
                        <Input id="my-input-1" aria-describedby="my-helper-text" />
                    </FormControl>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel htmlFor="my-input-2">Email</InputLabel>
                        <Input id="my-input-2" aria-describedby="my-helper-text" />
                        <FormHelperText id="my-helper-text">We'll never share your email.</FormHelperText>
                    </FormControl>
                    <FormControl sx={{
                        width: 270, m: 1
                    }}>
                        <InputLabel id="demo-multiple-name-label">Select a trainer</InputLabel>
                        <Select labelId="demo-multiple-name-label">

                        </Select>
                    </FormControl>
                </Box>






            </Container>
        </>
    );
}
