import api from '../../api';
import React, { useEffect, useState } from 'react';
import styles from "./HomePage.module.css";
import { Link } from 'react-router-dom';
import { Navigate, useNavigate } from 'react-router-dom';
import { Button, ModalBody, ModalHeader, Modal } from 'react-bootstrap';
import SubscriptionPage from '../subscriptions/SubscriptionPage';
import Notify from '../notifyuser/Notify';
import CatalogPage from '../catalog/CatalogPage';

const HomePage = () => {
  const [showLogoutNotification, setShowLogoutNotification] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(true);
  const [notifyState, setNotifyState] = useState(false);

  useEffect(() => {
    const logoutNotification = localStorage.getItem('logoutNotification');
    const notify = localStorage.getItem('notifyState');
    console.log(notify);
    if (notify === true) {
      setNotifyState(true);
    }

    if (logoutNotification) {
      setShowLogoutNotification(true);
      localStorage.removeItem('logoutNotification');
    }
  }, []);
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const navigate = useNavigate();

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="#">Navbar</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
  
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            <li className="nav-item">
              <Link to="/create-workout" className='nav-link'>Create workout</Link>
            </li>
            <li className="nav-item dropdown">
              {!showLogoutNotification && (
                <li><Link to="/account" className='nav-link'>Account</Link></li>
              )}
            </li>
            <li className="nav-item">
              <Link to="/login" className='nav-link'>Authorize</Link>
            </li>
          </ul>
          <form className="form-inline my-2 my-lg-0 mr-15">
            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
            <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
          </form>
        </div>
      </nav>
    </>
  );
  
};

export default HomePage;
