// AccountPage.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import 'bootstrap/dist/css/bootstrap.css';
import { Modal, Button } from 'react-bootstrap';
import { Navigate, useNavigate } from "react-router-dom";

const AccountPage = ({ isAccountDeleted, onDeleteAccount }) => {
  const [userData, setUserData] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    
    api.get('/user/data')
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account?')) {
      // Assuming there's a 'user_id' field in the user data
      const userId = userData && userData.user_id;

      if (userId) {
        api.delete(`/delete/${userId}`)
          .then(response => {
            setModalMessage('Account deleted successfully');
            setShowModal(true);
             // Trigger the callback to update the state in App component
            onDeleteAccount();
            navigate('/home');
          })
          .catch(error => {
            console.error('Error deleting account:', error);
          });
      }
    }
  };
  
  if (isAccountDeleted) {
    return <p>Account does not exist. Create a new account.</p>;
  }
  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/home')

  };
  return (
    <div className='container mt-5'>
      <h1>Account Page</h1>
      
      
      {userData && (
        <div>
          <p>Name: {userData.user_name}</p>
          <p>Email: {userData.user_email}</p>
          <p>Last workout check: {userData.last_check}</p>
          <button type="button"  className="btn btn-danger" onClick={handleDeleteAccount}>Delete account</button>
          <button type="button" className="btn btn-primary">Logout</button>

        </div>
      )}
      {/* Modal for successful deletion */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Success</Modal.Title>
        </Modal.Header>
        <Modal.Body>{modalMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
    
  );
};

export default AccountPage;
