import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css';
import { Modal } from 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { createBrowserHistory } from 'history';

axios.defaults.headers.common['X-CSRF-TOKEN'] = document.querySelector('meta[name="csrf-token"]').content;

const UserRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const [showModal, setShowModal] = useState(false);

  const history = createBrowserHistory();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    const modalElement = document.getElementById('registrationModal');
    const modal = new Modal(modalElement);

    const handleModalClose = () => {
      modal.hide();
      setShowModal(false);
      history.push('/home'); // Redirect to the main page after closing the modal
    };

    modal.show();

    // Listen for modal close event
    modalElement.addEventListener('hidden.bs.modal', handleModalClose);

    // Cleanup event listener on component unmount
    return () => {
      modalElement.removeEventListener('hidden.bs.modal', handleModalClose);
    };
  }, [showModal, history]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/register', formData);
      const { token } = response.data;

      localStorage.setItem('user_token', token);
      setShowModal(true);
    } catch (error) {
      console.error('Registration error:', error.response.data.message);
    }
  };

  return (
    <div className="container mt-5">
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} className="form-control" required />
        </div>

        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} className="form-control" required />
        </div>

        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} className="form-control" required />
        </div>

        <button type="submit" className="btn btn-primary">Register</button>
      </form>
       {/* Modal */}
       <div className="modal fade" id="registrationModal" tabIndex="-1" aria-labelledby="registrationModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="registrationModalLabel">Registration Successful</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <p>Your registration was successful.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
   
  );

};


export default UserRegistration;
