import { LOGIN_REQUEST, LOGIN_FAILURE, LOGIN_SUCCESS } from "./actions";

const defaultState = {
    isLoading: false,
    isLoggedIn: false,
    user: null,
    error: null
};

const authReducer = (state = defaultState, action) => {
    switch (action.type) {
        case LOGIN_REQUEST:
            return {...state, isLoading: true, error: null};
        case LOGIN_SUCCESS:
            return {...state, isLoading: false, error: null, isLoggedIn: true, user: action.payload};
        case LOGIN_FAILURE:
            return {...state, isLoading: false, error: action.payload, isLoggedIn: false}; 
        default:
            return state;
    }
};

export default authReducer;
