import React, { useState } from 'react';
import api from '../../api';
import { useNavigate } from "react-router-dom";
import styles from "./UserRegistration.module.css";

const UserRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    avatar: null,
  });

  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    if (e.target.name === 'avatar') {
      setFormData({ ...formData, avatar: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    navigate('/home');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('name', formData.name);
      formDataToSend.append('email', formData.email);
      formDataToSend.append('password', formData.password);
      formDataToSend.append('avatar', formData.avatar);

      const response = await api.post('/register', formDataToSend, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      
      const { token } = response.data;
      localStorage.setItem('user_token', token);
      localStorage.setItem('notifyState', 'true');
      
      setShowModal(true);
    } catch (error) {
      console.error('Registration error:', error.response.data.message);
    }
  };

  return (
    <div className={styles.main}>
      <div className={styles.container}>
        <form onSubmit={handleSubmit}>
          <h4>Create your own account</h4>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} className="form-control" required />
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} className="form-control" required />
          </div>

          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} className="form-control" required />
          </div>

          <div className="mb-3">
            <label htmlFor="avatar" className="form-label">Avatar</label>
            <input type="file" name="avatar" onChange={handleChange} className="form-control" accept="image/*" />
          </div>

          <button type="submit" className='btn btn-primary'>
            Create account
          </button>
        </form>
      </div>

      {/* <Modal show={showModal} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Registration Successful</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Your registration was successful.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleModalClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal> */}
    </div>
  );
};

export default UserRegistration;
