import React, { useEffect, useState } from "react";


import api from "../../api";

const CatalogPage = () => {
    const [catalogData, setCatalogData] = useState([]);
    const [url, setUrl] = useState("");

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/catalog');
                if (response.data) {
                    setCatalogData(response.data.data); // Assuming your data is nested under the 'data' key
                } else {
                    console.log("Response data is undefined:", response);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // Empty array as second parameter ensures this effect runs only once
{/* <div class="container text-center">
  <div class="row align-items-start">
    <div class="col">
      One of three columns
    </div>
    <div class="col">
      One of three columns
    </div>
    <div class="col">
      One of three columns
    </div>
  </div>
</div> */}
    return (
        <div className="container text-center">
           
            {catalogData.map(item => (
                
                <div className="col" key={item.id}>
                    <h2>{item.name}</h2>
                    <p>{item.description}</p>
                    <p>{item.difficulty}</p>
                   
                    <video width="560" height="315" controls>
                        <source src={`${item.video_link}`} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>


                </div>
            ))}
        </div>
    );

};


export default CatalogPage;
