import React, { useEffect, useState } from "react";
import api from "../../api";
import styles from "./SubscriptionPage.module.css";
import { Modal, Button } from "react-bootstrap";
import { redirect } from "react-router-dom";
import { Navigate, useNavigate } from 'react-router-dom';

const SubscriptionPage = () => {
    const [planData, setPlanData] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState(null);
    const [modal, setShowModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    let count = 1;
    let temp = true;
    useEffect(() => {
        api.get('/user/plans').then(response => {
            setPlanData(response.data['All plans: '] || []);
        });
    }, []);

    const redirectToPlansPage = (planId) => {
        const id = planId;
        navigate('/plans');
    }
    const handleSubscribe = () => {
        const planId = selectedPlan.id;
        if (planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    console.log(response);
                    setErrorMessage("Success");
                    setShowModal(true);
                })
                .catch(error => {
                    setErrorMessage(error.response.data.error);
                    setShowModal(true);

                });
        }
    }


    const closeModal = () => {
        setShowModal(false);
        setErrorMessage('');
    }

    return (
        <div>

            <div className="container text-center">
                <div className="row align-items-center">
                    {planData.map(plan => (
                        <div key={plan.id} className="col">
                            <h3>Available plan {count++} </h3>
                            <div>Name: {plan.name}</div>
                            <div>Price: {plan.price}</div>
                            <div>Duration: {plan.duration_month}</div>
                            <button
                                type="button"
                                className="btn btn-secondary"
                                onClick={redirectToPlansPage(plan.id)}
                            >
                                Select Plan
                            </button>
                        </div>
                    ))}
                </div>

            </div>

            {/* <button
                type="button"
                className="btn btn-primary"
                onClick={handleSubscribe}
                disabled={!selectedPlan}
            >
                Subscribe on Plan
            </button> */}

            {/* React Bootstrap Modal */}
            <Modal show={modal} onHide={closeModal}>
                <Modal.Header closeButton>

                </Modal.Header>
                <Modal.Body>
                    {temp ? (
                        <p>{errorMessage}</p>
                    ) : (
                        <p>Succes</p>
                    )}

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={closeModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>


        </div>
    );
};

export default SubscriptionPage;
