import axios from "axios";

const api = axios.create({
    baseURL: "/api",

});


api.interceptors.request.use((config) => {
    const userToken = localStorage.getItem('user_token');
    if (userToken){
        config.headers.Authorization = `Bearer ${userToken}`;
        console.log(userToken);
    }

    return config;
});

export default api;
