import React, { useState } from 'react';
import api from '../api';
import styles from './Modal.module.css';
import { createBrowserHistory } from 'history';

const CreateWorkoutsForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
  });

  const history = createBrowserHistory(); // React Router's useHistory hook
  const [isModalOpen, setModalOpen] = useState(true); // Initially set to true
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Assuming your API endpoint for creating workouts is "/user/workouts"
      const response = await api.post('/user/workouts', formData);
      console.log(response.data);

      // Close the modal (if it's part of your logic)
      // ...

      // Redirect to the main page
      history.push('/home');

    } catch (error) {
      console.error('Create workout error', error.response.data.message);
    }
  };

  return (
    // Conditionally render the modal based on the state
    isModalOpen && (
      <form onSubmit={handleSubmit} className={styles.centered}>
        <div className={styles.modalContent}>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />

          <label>Description</label>
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />

          <label>Date</label>
          <input
            type="text"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />

          <button type="submit" className={styles.primaryBtn}>
            Add
          </button>
        </div>
      </form>
    )
  );
};

export default CreateWorkoutsForm;
