import React, { useState } from 'react';
import { Form } from 'react-router-dom';
import api from '../api';
import { getElementError } from '@testing-library/react';


const LoginPage = () => {
    const [userData, setUserData] = useState({
        email: ' ',
        password: ' ',

    });
    const [isSuccessfullAuthorized, setSuccessfullAuthorized] = useState(false);

    const handleUserLogIn = async () => {
        const response = await api.post('/authorize', userData);
        setSuccessfullAuthorized(true);
        console.log(response.data);
        
    }

    const handleChange = (e) => {
        setUserData({ ...userData, [e.target.name]: e.target.value });
      };
    
    return (
        <form onSubmit={handleUserLogIn}>
            <div>
            <h1>Authorizing Page</h1>
            <label>Email: </label>
            <input type='email' name='email' value={userData.email} onChange={handleChange}></input>
            <label>Password: </label>
            <input type='password' name='password' value={userData.password} onChange={handleChange}></input>
            <button type='submit'>Log in</button>
        </div>

        </form>
        
       
        
    );
};


export default LoginPage;
