import { useState, useEffect } from "react";
import api from "../../api";
import { useLocation, useParams } from "react-router-dom";
import { BASE_URL } from "../constants";
import styles from "./PlansComp.module.css";
const PlansComp = () => {
    const [info, setInfo] = useState(null);
    const { planId } = useParams();
    const location = useLocation();
    const { planData } = location.state;
    useEffect(() => {
        const fetchData = async () => {
            try {

                const response = await api.get(`plan-info/${planId}`);
                const { trainer } = response.data;
                console.log(planData);
                setInfo(trainer);
            } catch (error) {
                console.error('Error fetching plan info:', error);
            }
        };

        fetchData();
    }, [planId]);
    if (!info) {
        return <div>Loading...</div>;
    }
    return (
        <>
            <div className={styles.container}>
                <div>
                    <h6>Information about chosen plan</h6>
                    <p>{planData.name}</p>
                    <p>Price: {planData.price}$</p>
                    <p>Duration: {planData.duration_months} months</p>
                </div>

                <div className={styles.trainersInfo}>
                    <h5>Trainer that available on this plan</h5>
                    <img src={`${BASE_URL}${info.image_path}`} alt="Cannot display image" width={200}></img>
                    <p>{info.description}</p>
                    <p>{info.year_exp} years of experience</p>
                    <blockquote class="blockquote text-center">
                        <p class="mb-3">{info.quote}</p>
                        <footer class="blockquote-footer">{info.name} {info.surname}</footer>
                    </blockquote>
                    <button className="btn btn-primary">Subscribe</button>
                </div>
            </div>
        </>
    );
}




export default PlansComp;
