import React, { useEffect, useState } from "react";
import styles from './CatalogPage.module.css';

import api from "../../api";

const CatalogPage = () => {
    const [catalogData, setCatalogData] = useState([]);
   

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/catalog');
                if (response.data) {
                    setCatalogData(response.data.data); // Assuming your data is nested under the 'data' key
                } else {
                    console.log("Response data is undefined:", response);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // Empty array as second parameter ensures this effect runs only once

    return (
        <div className="container text-center">
    <div className="row align-items-center">
        {catalogData.map(item => (
            <div className="col" key={item.id}>
                <h2>{item.name}</h2>
                <p className="content">{item.description}</p>
                <p className="content">{item.difficulty}</p>
                <div className="video-wrapper">
                    <video autoPlay="autoplay" className="video">
                        <source src={`${item.video_link}`} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                </div>
            </div>
        ))}
    </div>
</div>

    );

};


export default CatalogPage;
