import React, { useState, useEffect } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';
import WorkoutCreateModal from './modals/WorkoutCreateModal';
import api from '../api';

const CreateWorkoutsForm = () => {
  const [isWorkoutCreated, setIsWorkoutCreated] = useState(false);
  const navigate = useNavigate();
  const [workoutData, setWorkoutData] = useState({
    title: '',
    description: '',
    date: ''
  });

  const handleChange = (e) => {
    setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    if (isWorkoutCreated) {
      // Вызываем navigate только после изменения isWorkoutCreated
      navigate('/home');
    }
  }, [isWorkoutCreated, navigate]);

  const handleSubmit = async (e) => {
    const response = await api.post('/user/workouts', workoutData);
    setIsWorkoutCreated(true);
  };


  return (
    <div>
      <label>Title</label>
      <input type='text' name='title' value={workoutData.title} onChange={handleChange}></input>
      <label>Description</label>
      <input type='text' name='description' value={workoutData.description} onChange={handleChange}></input>
      <label>Date</label>
      <input type='text' name='date' value={workoutData.date} onChange={handleChange}></input>
      <button type='submit' onClick={handleSubmit}>
        Add workout
      </button>

     
    </div>
  );
};

export default CreateWorkoutsForm;
