import React, { useState, useEffect } from 'react';
import { ListItem, Typography, Boxn } from "@mui/material";
import api from "../../api";
import ClearIcon from '@mui/icons-material/Clear';
export default function Notify() {
    const [notifyData, setNotifyData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get('/get/requests');
                setNotifyData(response.data.success);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchData();
    }, []); 

    return (
        <Box>
            {notifyData.map((item) => (
                <Box key={item.id}>
                    <ListItem alignItems="flex-start">
                        <Typography>Notification1 {item.created_at} {item.status}
                        <ClearIcon 
                            color={isHovered ? "primary" : "disabled"} 
                            sx={{ position: 'absolute', top: 5, right: 5, cursor: 'pointer' }} 
                            onClick={() => handleDeleteComment(item.id)} 
                        />
                        </Typography>
                    </ListItem>
                  
                </Box>
            ))}
        </Box>
    )
}
