// AccountPage.js
import React, { useState, useEffect } from 'react';
import api from '../../api';
import 'bootstrap/dist/css/bootstrap.css';
import { Modal, Button } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';
import SubsComponent from '../subscriptions/SubsComponent';
import UserWorkouts from '../workouts/UserWorkouts';
import styles from "./AccountPage.module.css";


const AccountPage = () => {
  const [userData, setUserData] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  
  const navigate = useNavigate();

  useEffect(() => {
    
    api.get('/user/data')
      .then(response => {
        // console.log(response.data);
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setError("Account not found");
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account?')) {

      const userId = userData && userData.user_id;

      if (userId) {
     
        api.delete(`/subscription/delete/${userId}`)
          .then(() => {
            // Now, delete the user
            return api.delete(`/delete/${userId}`);
          })
          .then(response => {
            setModalMessage('Account deleted successfully');
            setShowModal(true);
            navigate('/home');
          })
          .catch(error => {
            console.error('Error deleting account:', error);
          });
    }
  };
}
  const handleLogoutAccount = () => {
    localStorage.setItem('logoutNotification', 'true');
    setModalMessage('Logout successfully');
    setShowModal(true);
 
  };

  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/home');

  };
 
if (!userData || loading) {
   return (
    <div>
      {error}
    </div>

   );


}

  return (
    <div className={styles.container}>

      
      
      {userData && (
        <div className={styles.userInfo}>
          <p>Name: {userData.user_name}</p>
          <p>Email: {userData.user_email}</p>
          <p>Last workout check: {userData.last_check}</p>
          <SubsComponent />
          <button type="button"  className="btn btn-danger" onClick={handleDeleteAccount}>Delete account</button>
          <button type="button" className="btn btn-primary" onClick={handleLogoutAccount}>Logout</button>
          
        </div>
       
      )}
      
      <div className={styles.userWorkouts}>
        <UserWorkouts></UserWorkouts>
      </div>
  

      {/* Modal for successful deletion */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Success</Modal.Title>
        </Modal.Header>
        <Modal.Body>{modalMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default AccountPage;
