import { useState, useEffect } from "react";
import api from "../../api";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { BASE_URL } from "../constants";
import styles from "./PlansComp.module.css";
import FeedBack from "./FeedBack";
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';
const PlansComp = () => {
    const [info, setInfo] = useState(null);
    const { planId } = useParams();
    const [errorData, setErrorData] = useState("");
    const location = useLocation();
    const navigate = useNavigate();
    const { planData } = location.state;
    useEffect(() => {
        const fetchData = async () => {
            try {

                const response = await api.get(`plan-info/${planId}`);
                const { trainer } = response.data;
                // console.log(planData);
                setInfo(trainer);
            } catch (error) {
                console.error('Error fetching plan info:', error);
            }
        };

        fetchData();
    }, [planId]);




    const handleSubscribe = () => {

        if (planId) {
            api.post(`/user/add-subscription/${planId}`)
                .then(response => {
                    setErrorData("Successfully subscribed to this plan");

                    navigate('/home');

                })
                .catch(error => {
                    if (error.response.status == 401) {
                    

                        setErrorData(error.response.data.message);
                      
                    }
                    else {
                        
                        setErrorData(error.response.data.error);
                       


                    }

                });
        }
    }
    if (!info) {
        return <div>Loading...</div>;
    }
    return (
        <>
            <div className={styles.container}>
                <div className={styles.planInfo}>
                    <h5>Information about chosen plan</h5>
                    <p>{planData.name}</p>
                    <p>Price: {planData.price}$</p>
                    <p>Duration: {planData.duration_months} months</p>
                </div>

                <div className={styles.trainersInfo}>
                    <h5>Trainer that available on this plan</h5>
                    <img src={`${BASE_URL}${info.image_path}`} alt="Cannot display image" width={200}></img>

                    <p>{info.description}</p>
                    <p>{info.year_exp} years of experience</p>
                    <blockquote>
                        <p class="mb-3">{info.quote}</p>
                        <footer>{info.name} {info.surname}</footer>
                    </blockquote>


                    <button className="btn btn-primary" onClick={() => handleSubscribe()}>Subscribe</button>

                </div>
                <p>Users comments subscribed to this plan</p>
                <FeedBack planId={planId} trainerId={planData.trainer_id} />

            </div>
            <Alert icon={<CheckIcon fontSize="inherit" />} severity="success">
                Here is a gentle confirmation that your action was successful.
            </Alert>


        </>
    );
}




export default PlansComp;
