// AccountPage.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import 'bootstrap/dist/css/bootstrap.css';
import { Modal, Button } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';


const AccountPage = () => {
  const [userData, setUserData] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    
    api.get('/user/data')
      .then(response => {
        console.log(response.data);
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account?')) {
      // Assuming there's a 'user_id' field in the user data
      const userId = userData && userData.user_id;

      if (userId) {
        api.delete(`/delete/${userId}`)
          .then(response => {
            setModalMessage('Account deleted successfully');
            setShowModal(true);
            
            navigate('/home');
          })
          .catch(error => {
            console.error('Error deleting account:', error);
          });
      }
    }
  };
  
  
  const handleCloseModal = () => {
    setShowModal(false);
    navigate('/home');

  };
  // Redirect to home if user data is not available (account deleted)
if (!userData || loading) {
  return <div>Loading...</div>; // You can render a loading spinner or message here
  // return <Navigate to="/home" />;
}

  return (
    <div className='container mt-5'>
      <h1>Account Page</h1>
      
      
      {userData && (
        <div>
          <p>Name: {userData.user_name}</p>
          <p>Email: {userData.user_email}</p>
          <p>Last workout check: {userData.last_check}</p>
          <button type="button"  className="btn btn-danger" onClick={handleDeleteAccount}>Delete account</button>
          <button type="button" className="btn btn-primary" onClick={handle}>Logout</button>

        </div>
      )}
      {/* Modal for successful deletion */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Success</Modal.Title>
        </Modal.Header>
        <Modal.Body>{modalMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
    
  );
};

export default AccountPage;
