import { Box, Typography, Divider, Alert, Button } from "@mui/material";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../../api";
import { BASE_URL } from "../constants";

export default function Workouts() {
    const { catalogId } = useParams();
    const [workouts, setWorkouts] = useState([]);
    const [alert, setAlertShow] = useState(false);
    const [alertSeverity, setAlertSeverity] = useState("");
    const [alertMessage, setAlertMessage] = useState("");

    const handleAddWorkout = async (id) => {
        try {
            const response = await api.post(`/add-workout/${id}`);
            setAlertSeverity("success");
            setAlertMessage("Success");
            setAlertShow(true);
        } catch(error) {
            setAlertSeverity("error");
            setAlertMessage(error.response.data.message); 
            setAlertShow(true);
        }
    }

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await api.get(`/get-workout/${catalogId}`);
                setWorkouts(response.data.workouts);
            } catch (error) {
                console.error(error);
            }
        };

        fetchData();
    }, [catalogId]);

    return (
        <Box>
            {/* Отображаем алерт, если состояние alert равно true */}
            {alert && (
                <Alert
                    severity={alertSeverity}
                    onClose={() => { setAlertShow(false) }}
                    sx={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 9999 }}
                >
                    {alertMessage}
                </Alert>
            )}
            {/* Отображаем тренировки */}
            {workouts.map((workout) => (
                <Box key={workout.id} sx={{ border: 1, p: 5, mb: 2 }}>
                    <Typography variant="h5">{workout.title}</Typography>
                    <Typography variant="body1">{workout.description}</Typography>
                    <Divider />
                    <Typography variant="body2">Date: {workout.date}</Typography>
                    {/* Добавляем кнопку для добавления тренировки */}
                    <Button onClick={() => handleAddWorkout(workout.id)} variant="outlined">Add</Button>
                </Box>
            ))}
        </Box>
    );
}
