import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api';

const CreateWorkoutsForm = () => {
  const navigate = useNavigate();
  const [workoutData, setWorkoutData] = useState({
    title: '',
    description: '',
    date: ''
  });

  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('title', workoutData.title);
      formData.append('description', workoutData.description);
      formData.append('date', workoutData.date);
      formData.append('image', imageFile);

      const response = await api.post('/user/workouts', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      navigate('/home');
    } catch (error) {
      console.error('Error adding workout:', error);
    }
  };

  return (
    <div className="container mt-5">
      <form>
      <div className="mb-3">
        <label htmlFor="title" className="form-label">
          Title
        </label>
        <input
          type="text"
          className="form-control"
          id="title"
          name="title"
          value={workoutData.title}
          onChange={handleChange}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">
          Description
        </label>
        <textarea
          
          className="form-control"
          id="description"
          name="description"
          value={workoutData.description}
          onChange={handleChange}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="date" className="form-label">
          Date
        </label>
        <input
          type="text"
          className="form-control"
          id="date"
          name="date"
          value={workoutData.date}
          onChange={handleChange}
        />
      <input type="file" name="image" onChange={handleImageChange}/>

      </div>

      <button type="submit" className="btn btn-primary" onClick={handleSubmit}>
        Add Workout
      </button>
      
      </form>
    </div>
  );
};

export default CreateWorkoutsForm;
